  "campaignId": 94762,
  "customerId": "633",
  "recipients": [
    {
      "recipientData": [
        {
          "label": "forename",
          "value": "Chris"
        },
        {
          "label": "zip",
          "value": "48309"
        },
        {
          "label": "street",
          "value": "Baker Street"
        },
        {
          "label": "city",
          "value": "Dover"
        },
        {
          "label": "individual_image_var",
          "value": ""
