        <td width="50%" rowspan=6>

        </td>
        <td width="50%" class="hr pr bold fl">{{ customer|format_customer_name }}</td>
      </tr>
      <tr>
        <td width="50%" class="hr pr">{{ customer.homeAddress|format_street }}</td>
      </tr>
