  "trackingIdentifier": "REPLACE-WITH-YOUR-TRACKING-IDENTIFIER",
  "eventCode": "AWAITING_COLLECTION_FROM_PICKUP_POINT",
  "eventDate": "2023-06-13T13:36:29.043Z",
  "eventTimeZone": "Europe/London",
  "eventLocation": {
    "address": {
      "city": "London",
      "countryCode": "GBR",
      "postCode": "WC1X 8XZ",
