            "patternProperties": {
                "^(is|is_not|contain|not_contain|is_empty)$": {
                    "minLength": 2,
                    "required": true,
                    "type": ["string", "boolean"]
                }
            },
            "type": "object"
        }
    },
    "type": "object",
    "description": "/twitter/linkedin/facebook/phone/last name/street/city/state/zip/country/company name/title validation rule"
}

