    // ..."overrides": { // note that this differs from the JSON in the request
        "recipientName": "Override Name",
        "recipientNotes": null,
        "recipientSkipSMSNotifications": null,
        "useMerchantForProxy": null
    },
    // ..."recipients": [
        {
            "id": "v7lVeNMHWsDwt*WuzcLmTAbg",
